"use client";
import { useState } from "react";

const Header = ({ onSearch }) => {
  const [query, setQuery] = useState("");

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(query); // Send search term to App
  };

  return (
    <header className="header p-4 bg-gray-100 flex justify-between items-center">
      <h1 className="text-xl font-bold">EventApp</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search events..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="border rounded px-2 py-1"
        />
        <button type="submit" className="ml-2 btn btn-primary">Search</button>
      </form>
    </header>
  );
};

export default Header;
