import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { eventService, bookingService } from "../services/api";
import { formatPrice } from "../utils/format";

const BookingPage = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [booking, setBooking] = useState(false);
  const [seats, setSeats] = useState(1);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user) return navigate("/login");

    const fetchEvent = async () => {
      try {
        const res = await eventService.getEventById(id);
        setEvent(res.data);
      } catch {
        setError("Failed to load event");
      } finally {
        setLoading(false);
      }
    };
    fetchEvent();
  }, [id, user, navigate]);

  const handleBooking = async (e) => {
    e.preventDefault();
    setBooking(true);
    setError(null);

    try {
      await bookingService.createBooking({
        eventId: Number(id),
        seatsBooked: seats,
        totalAmount: Number(event.price) * seats,
      });
      navigate("/"); // or redirect to confirmation page
    } catch (err) {
      setError(err.response?.data?.message || "Booking failed");
    } finally {
      setBooking(false);
    }
  };

  if (loading) return <div className="container py-12 text-center">Loading booking...</div>;
  if (error && !event) return <div className="container py-12 text-center text-red-500">{error}</div>;

  const totalAmount = Number(event.price) * seats;

  return (
    <div className="container py-12 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold text-center mb-8">Complete Your Booking</h1>
      <div className="grid lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-6">
          {/* Select Tickets */}
          <div>
            <h2 className="text-xl font-semibold mb-4">Select Tickets</h2>
            <div className="card p-4 flex justify-between items-center">
              <div>
                <p className="font-medium">{event.title}</p>
                <p className="text-gray-500">{formatPrice(event.price)} per ticket</p>
              </div>
              <div className="flex items-center gap-2">
                <button onClick={() => setSeats(Math.max(1, seats - 1))} className="btn btn-secondary">-</button>
                <span>{seats}</span>
                <button onClick={() => setSeats(Math.min(event.availableSeats, seats + 1))} className="btn btn-secondary">+</button>
              </div>
            </div>
          </div>

          {/* User Details */}
          <div>
            <h2 className="text-xl font-semibold mb-4">Confirm Your Details</h2>
            <div className="card p-4">
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p className="text-gray-500 text-sm">Booking confirmation will be sent here.</p>
            </div>
          </div>

          {/* Confirm Booking */}
          <div>
            {error && <div className="text-red-500 mb-2">{error}</div>}
            <form onSubmit={handleBooking}>
              <button type="submit" className="btn btn-primary w-full">
                {booking ? "Processing..." : `Confirm Booking - ${formatPrice(totalAmount)}`}
              </button>
            </form>
          </div>
        </div>

        {/* Order Summary */}
        <div>
          <div className="card p-6 sticky top-8">
            <h3 className="text-lg font-semibold mb-4">Order Summary</h3>
            <div className="flex justify-between mb-2">
              <span>Tickets ({seats}x)</span>
              <span>{formatPrice(totalAmount)}</span>
            </div>
            <div className="border-t pt-4 flex justify-between font-semibold text-lg">
              <span>Total</span>
              <span>{formatPrice(totalAmount)}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingPage;
