import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { eventService } from "../services/api";
import { formatDate, formatPrice } from "../utils/format";

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await eventService.getAllEvents();
        setEvents(res.data);
      } catch {
        setError("Failed to load events");
      } finally {
        setLoading(false);
      }
    };
    fetchEvents();
  }, []);

  if (loading) return <div className="container py-12 text-center">Loading events...</div>;
  if (error) return <div className="container py-12 text-center text-red-500">{error}</div>;

  return (
    <div className="container py-12">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Discover Amazing Events</h1>
        <p className="text-gray-500 text-lg">Find and book tickets for the best events happening around you</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {events.map((event) => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
    </div>
  );
};

const EventCard = ({ event }) => (
  <Link to={`/events/${event.id}`} className="block">
    <div className="card hover:-translate-y-1 transition-transform cursor-pointer">
      <img
        src={event.imageUrl || "/placeholder.svg"}
        alt={event.title}
        className="w-full h-48 object-cover rounded mb-4"
      />
      <span className="text-sm bg-gray-100 px-2 py-1 rounded text-gray-600">{event.category}</span>
      <h3 className="text-xl font-semibold mt-2">{event.title}</h3>
      <p className="text-gray-500 text-sm mb-2">{event.venue}</p>
      <div className="flex justify-between items-center">
        <span className="text-sm text-gray-500">{formatDate(event.eventDate)}</span>
        <span className="text-lg font-semibold">{formatPrice(event.price)}</span>
      </div>
      <div className="text-sm text-gray-400">{event.availableSeats} seats left</div>
    </div>
  </Link>
);

export default EventList;
