"use client"

import { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import { eventService, bookingService } from "../services/api"
import { useAuth } from "../context/AuthContext"

const BookingPage = () => {
  const { id } = useParams()
  const { user } = useAuth()
  const navigate = useNavigate()
  const [event, setEvent] = useState(null)
  const [loading, setLoading] = useState(true)
  const [booking, setBooking] = useState(false)
  const [seats, setSeats] = useState(1)
  const [error, setError] = useState(null)

  useEffect(() => {
    if (!user) {
      navigate("/login")
      return
    }

    const fetchEvent = async () => {
      try {
        const response = await eventService.getEventById(id)
        setEvent(response.data)
      } catch {
        setError("Failed to load event details")
      } finally {
        setLoading(false)
      }
    }

    fetchEvent()
  }, [id, user, navigate])

  const handleBooking = async (e) => {
    e.preventDefault()
    setBooking(true)
    setError(null)

    try {
      const bookingData = {
        eventId: Number.parseInt(id),
        seatsBooked: seats,
        totalAmount: event.price * seats,
      }
      await bookingService.createBooking(bookingData)
      alert("Booking successful! Check your email for confirmation.")
      navigate("/")
    } catch (err) {
      setError(err.response?.data || "Booking failed. Please try again.")
    } finally {
      setBooking(false)
    }
  }

  if (loading) return <div style={{ padding: "2rem", textAlign: "center" }}>Loading booking details...</div>
  if (error && !event) return <div style={{ padding: "2rem", textAlign: "center", color: "red" }}>{error}</div>

  const formatPrice = (price) => new Intl.NumberFormat("en-US", { style: "currency", currency: "USD" }).format(price)
  const totalAmount = event ? event.price * seats : 0

  return (
    <div style={{ padding: "2rem", maxWidth: "900px", margin: "0 auto" }}>
      <h1 style={{ fontSize: "2rem", fontWeight: "bold", marginBottom: "2rem", textAlign: "center" }}>
        Complete Your Booking
      </h1>

      <div style={{ display: "flex", gap: "2rem", flexWrap: "wrap" }}>
        <div style={{ flex: "2 1 60%" }}>
          {/* Step 1 */}
          <div style={{ marginBottom: "2rem" }}>
            <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1rem" }}>
              <div style={{
                width: "32px", height: "32px", backgroundColor: "#007bff", color: "#fff",
                borderRadius: "50%", display: "flex", alignItems: "center", justifyContent: "center",
                fontWeight: "bold"
              }}>1</div>
              <h2 style={{ fontSize: "1.25rem", fontWeight: "600" }}>Select your tickets</h2>
            </div>

            {event && (
              <div style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "1rem" }}>
                <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1rem" }}>
                  <img
                    src={event.imageUrl || "/placeholder.svg"}
                    alt={event.title}
                    style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "8px" }}
                  />
                  <div>
                    <h3 style={{ fontWeight: "600" }}>{event.title}</h3>
                    <p style={{ fontSize: "0.875rem", color: "#666" }}>{event.venue}</p>
                    <p style={{ fontSize: "0.875rem", color: "#666" }}>{new Date(event.eventDate).toLocaleDateString()}</p>
                  </div>
                </div>

                <div style={{ display: "flex", justifyContent: "space-between", padding: "0.5rem 1rem", backgroundColor: "#f0f0f0", borderRadius: "8px" }}>
                  <div>
                    <p style={{ fontWeight: "500" }}>General Admission</p>
                    <p style={{ fontSize: "0.875rem", color: "#666" }}>{formatPrice(event.price)} per ticket</p>
                  </div>
                  <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
                    <button type="button" onClick={() => setSeats(Math.max(1, seats - 1))}>-</button>
                    <span style={{ width: "24px", textAlign: "center" }}>{seats}</span>
                    <button type="button" onClick={() => setSeats(Math.min(event.availableSeats, seats + 1))}>+</button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Step 2 */}
          <div style={{ marginBottom: "2rem" }}>
            <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1rem" }}>
              <div style={{
                width: "32px", height: "32px", backgroundColor: "#007bff", color: "#fff",
                borderRadius: "50%", display: "flex", alignItems: "center", justifyContent: "center",
                fontWeight: "bold"
              }}>2</div>
              <h2 style={{ fontSize: "1.25rem", fontWeight: "600" }}>Confirm your details</h2>
            </div>

            <div style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "1rem" }}>
              <p><strong>Name:</strong> {user?.name}</p>
              <p><strong>Email:</strong> {user?.email}</p>
              <p style={{ fontSize: "0.875rem", color: "#666" }}>Booking confirmation will be sent to this email address.</p>
            </div>
          </div>

          {/* Step 3 */}
          <div>
            <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1rem" }}>
              <div style={{
                width: "32px", height: "32px", backgroundColor: "#007bff", color: "#fff",
                borderRadius: "50%", display: "flex", alignItems: "center", justifyContent: "center",
                fontWeight: "bold"
              }}>3</div>
              <h2 style={{ fontSize: "1.25rem", fontWeight: "600" }}>Complete booking</h2>
            </div>

            {error && <div style={{ marginBottom: "1rem", padding: "0.5rem", color: "red", border: "1px solid red", borderRadius: "4px" }}>{error}</div>}

            <form onSubmit={handleBooking}>
              <button type="submit" disabled={booking} style={{ width: "100%", padding: "0.75rem", backgroundColor: "#007bff", color: "#fff", border: "none", borderRadius: "4px" }}>
                {booking ? "Processing..." : `Confirm Booking - ${formatPrice(totalAmount)}`}
              </button>
            </form>
          </div>
        </div>

        {/* Order Summary */}
        <div style={{ flex: "1 1 35%" }}>
          <div style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "1rem", position: "sticky", top: "1rem" }}>
            <h3 style={{ fontWeight: "600", marginBottom: "1rem" }}>Order Summary</h3>
            {event && (
              <>
                <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "0.5rem" }}>
                  <span>Tickets ({seats}x)</span>
                  <span>{formatPrice(event.price * seats)}</span>
                </div>
                <div style={{ borderTop: "1px solid #ccc", paddingTop: "0.5rem", fontWeight: "600", fontSize: "1rem", display: "flex", justifyContent: "space-between" }}>
                  <span>Total</span>
                  <span>{formatPrice(totalAmount)}</span>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  )
}

export default BookingPage
