import { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { eventService } from "../services/api";
import { useAuth } from "../context/AuthContext";
import { formatDate, formatPrice } from "../utils/format";

const EventDetail = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const res = await eventService.getEventById(id);
        setEvent(res.data);
      } catch {
        setError("Failed to load event details");
      } finally {
        setLoading(false);
      }
    };
    fetchEvent();
  }, [id]);

  const handleBookNow = () => {
    if (!user) return navigate("/login");
    navigate(`/booking/${id}`);
  };

  if (loading) return <div className="container py-12 text-center">Loading event details...</div>;
  if (error || !event) return <div className="container py-12 text-center text-red-500">{error || "Event not found"}</div>;

  return (
    <div className="container py-12 grid lg:grid-cols-3 gap-8">
      <div className="lg:col-span-2">
        <img
          src={event.imageUrl || "/placeholder.svg"}
          alt={event.title}
          className="w-full h-96 object-cover rounded mb-6"
        />
        <span className="text-sm bg-gray-100 px-2 py-1 rounded text-gray-600">{event.category}</span>
        <h1 className="text-3xl font-bold mb-4">{event.title}</h1>
        <p className="text-gray-500 mb-4">{event.description}</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="card p-4">
            <h3 className="font-semibold mb-2">📅 Date & Time</h3>
            <p className="text-gray-500">{formatDate(event.eventDate)}</p>
          </div>
          <div className="card p-4">
            <h3 className="font-semibold mb-2">📍 Venue</h3>
            <p className="text-gray-500">{event.venue}</p>
          </div>
        </div>
      </div>

      <div>
        <div className="card p-6 sticky top-8">
          <h2 className="text-2xl font-bold mb-4">Book Your Tickets</h2>
          <div className="mb-6 flex justify-between items-center">
            <span className="text-lg">Ticket Price</span>
            <span className="text-2xl font-bold">{formatPrice(event.price)}</span>
          </div>
          <p className="text-sm text-gray-500 mb-4">{event.availableSeats} of {event.totalSeats} seats available</p>
          <button
            onClick={handleBookNow}
            className="btn btn-primary w-full"
            disabled={event.availableSeats === 0}
          >
            {event.availableSeats === 0 ? "Sold Out" : "Book Now"}
          </button>
          <Link to="/" className="text-sm text-gray-500 block mt-4 text-center underline">← Back to all events</Link>
        </div>
      </div>
    </div>
  );
};

export default EventDetail;
