"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { eventService } from "../services/api"
import rockConcertImg from "../assets/images/RockConcert.jpg"
import techConferenceImg from "../assets/images/techconferance.jpeg"
import comedyNightImg from "../assets/images/comedynight.jpg"
import foodFestivalImg from "../assets/images/foodfestival.jpg"
import artExhibitionImg from "../assets/images/artexhibition.jpg"
import backgroundimage from "../assets/images/backgroundimage.jpg"

// Move eventImages here so it's accessible everywhere
const eventImages = {
  1: rockConcertImg,
  2: techConferenceImg,
  3: comedyNightImg,
  4: foodFestivalImg,
  5: artExhibitionImg,
}

const EventList = ({ searchTerm }) => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await eventService.getAllEvents();
        setEvents(response.data);
      } catch (err) {
        setError("Failed to load events");
      } finally {
        setLoading(false);
      }
    };
    fetchEvents();
  }, []);

  // Filter events by searchTerm
  const filteredEvents = events.filter((event) =>
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return <div className="text-center py-12">Loading events...</div>;
  if (error) return <div className="text-center py-12 text-destructive">{error}</div>;

  return (
    <div className="container py-12">
      {filteredEvents.length === 0 && (
        <div className="text-center text-muted">No events found.</div>
      )}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredEvents.map((event) => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
    </div>
  );
};
