import React, { useState } from "react";

const DistanceCalculator = () => {
  const [distance, setDistance] = useState("");
  const [duration, setDuration] = useState("");

  const calculateDistance = async () => {
    const origin = "New York, NY";       // You can use lat,lng too
    const destination = "Los Angeles, CA";

    const response = await fetch(
      `https://maps.googleapis.com/maps/api/distancematrix/json?units=metric&origins=${origin}&destinations=${destination}&key=YOUR_API_KEY`
    );

    const data = await response.json();

    if (data.rows[0].elements[0].status === "OK") {
      setDistance(data.rows[0].elements[0].distance.text);
      setDuration(data.rows[0].elements[0].duration.text);
    } else {
      setDistance("Distance not available");
      setDuration("Duration not available");
    }
  };

  return (
    <div>
      <button onClick={calculateDistance}>Calculate Distance</button>
      {distance && (
        <p>
          Distance: {distance}, Duration: {duration}
        </p>
      )}
    </div>
  );
};

export default DistanceCalculator;
