"use client"
import { useState, useEffect } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import { eventService } from "../services/api" // make sure your API supports search

const Header = ({ onSearch }) => {
  const { user, logout } = useAuth()
  const navigate = useNavigate()
  const [searchTerm, setSearchTerm] = useState("")
  const [searchResults, setSearchResults] = useState([])
  const [showResults, setShowResults] = useState(false)

  const handleLogout = () => {
    logout()
    navigate("/")
  }

  // Update parent App component with search term
  useEffect(() => {
    if (onSearch) {
      onSearch(searchTerm)
    }
  }, [searchTerm, onSearch])

  // Optional: fetch live search suggestions from API
  useEffect(() => {
    const fetchResults = async () => {
      if (!searchTerm.trim()) {
        setSearchResults([])
        return
      }

      try {
        const response = await eventService.searchEvents(searchTerm)
        setSearchResults(response.data || [])
      } catch (err) {
        console.error("Search failed", err)
      }
    }

    fetchResults()
  }, [searchTerm])

  const handleSelectResult = (eventId) => {
    navigate(`/events/${eventId}`)
    setSearchTerm("")
    setSearchResults([])
    setShowResults(false)
  }

  return (
    <header>
      <div className="container">
        <nav className="flex items-center justify-between py-2 relative">
          <Link
            to="/"
            className="text-xl font-bold"
            style={{ textDecoration: "none", color: "var(--foreground)" }}
          >
            Event Connect
          </Link>

          {/* Search Bar */}
          <div className="relative">
            <input
              type="text"
              placeholder="Search by title, venue, category..."
              className="border rounded px-3 py-1"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onFocus={() => setShowResults(true)}
              onBlur={() => setTimeout(() => setShowResults(false), 200)} // allow click
            />

            {/* Search Results Dropdown */}
            {/* {showResults && searchResults.length > 0 && (
              <ul className="absolute bg-white border rounded w-full mt-1 z-50 max-h-60 overflow-y-auto shadow-lg">
                {searchResults.map((event) => (
                  <li
                    key={event.id}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => handleSelectResult(event.id)}
                  >
                    <strong>{event.title}</strong> - {event.venue} ({event.category})
                  </li>
                ))}
              </ul>
            )} */}
          </div>

          <div className="hidden md:flex items-center gap-6">
            <Link to="/" style={{ textDecoration: "none", color: "var(--muted-foreground)" }}>
              Events
            </Link>
            <Link to="/" style={{ textDecoration: "none", color: "var(--muted-foreground)" }}>
              Categories
            </Link>
            <Link to="/" style={{ textDecoration: "none", color: "var(--muted-foreground)" }}>
              Popular
            </Link>
          </div>

          <div className="flex items-center gap-4">
            {user ? (
              <>
                <span className="text-sm text-muted">Welcome, {user.name}</span>
                <button onClick={handleLogout} className="btn btn-secondary">
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="btn btn-secondary">
                  Login
                </Link>
                <Link to="/register" className="btn btn-primary">
                  Sign Up
                </Link>
              </>
            )}
          </div>
        </nav>
      </div>
    </header>
  )
}

export default Header
