   bold: element.bold,
   size: `${pxToPtHandler(element.size)}pt`,
   color: getHexColor(element.color),
   italics: element.italic,
   strike: element.strikeout,
   highlight: element.highlight ? Color(element.highlight).hex() : undefined,
