     appendParagraph(); // Separate list from previous content
   
     const valueList = element.valueList || [];
     const isDecimal = element.listStyle === ListStyle.DECIMAL;
   
     valueList.reduce((count, cur) => {
       if (cur.value !== '\n') {
         cur.value
           .replace(/^\n/, '') // Remove leading newline
           .split('\n')        // Handle multiline entries
           .forEach(text => {
             children.push(
               new Paragraph({
                 alignment: cur.rowFlex
                   ? RowFlexToAlignmentType[cur.rowFlex]
                   : undefined,
                 children: [
                   new TextRun({
                     font: cur.font||'Arial',
                     text: `${isDecimal ? `${++count}. ` : `• `}${text}`,
