                   })
                 ], spacing: { after: 200 }
               })
             );
           });
       }else if (cur.value  === '\n') {
         // Single newline - add line break if we have content
         if (paragraphChild.length > 0) {
           paragraphChild.push(new TextRun({ text: '', break: 1 }));
         }
       } 
       else if (cur.value  === '\n\n') {
         // Double newline - force new paragraph
         appendParagraph();
