"use client"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import { useEffect, useState } from "react"

const Header = () => {
  const { user, logout } = useAuth()
  const navigate = useNavigate()
  const [search, setSearch] = useState("")

  const handleLogout = () => {
    logout()
    navigate("/")
  }

  useEffect(() => {
    const handler = e => setSearch(e.detail)
    window.addEventListener("searchEvent", handler)
    return () => window.removeEventListener("searchEvent", handler)
  }, [])

  return (
    <header style={{ borderBottom: "1px solid var(--border)" }}>
      <div className="container">
        <nav className="flex items-center justify-between py-4">
          <Link to="/" className="text-xl font-bold" style={{ textDecoration: "none", color: "var(--foreground)" }}>
            Event Connect
          </Link>

          <div className="hidden md:flex items-center gap-6">
            <Link to="/" style={{ textDecoration: "none", color: "var(--muted-foreground)" }}>
              Events
            </Link>
            {/* Search input for events */}
            <input
              type="text"
              placeholder="Search events..."
              className="px-3 py-2 rounded border"
              style={{ minWidth: "200px" }}
              onChange={e => {
                // Use a custom event for search, or update global state/context as needed
                window.dispatchEvent(new CustomEvent("searchEvent", { detail: e.target.value }))
              }}
            />
            <Link to="/popular" style={{ textDecoration: "none", color: "var(--muted-foreground)" }}>
              Popular
            </Link>
          </div>

          <div className="flex items-center gap-4">
            {user ? (
              <>
                <span className="text-sm text-muted">Welcome, {user.name}</span>
                <button onClick={handleLogout} className="btn btn-secondary">
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="btn btn-secondary">
                  Login
                </Link>
                <Link to="/register" className="btn btn-primary">
                  Sign Up
                </Link>
              </>
            )}
          </div>
        </nav>
      </div>
    </header>
  )
}

export default Header
